	UserId                  IN  NVARCHAR2,
	ProgramId               IN  NVARCHAR2,
	WorkStationId           IN  NVARCHAR2,
	DateUpdated             IN  DECIMAL,
	TimeOfDay               IN  DECIMAL
)	
AS 
BEGIN
