AS
BEGIN		
	--CATALOG COPY
	INSERT INTO [SCDATA].FQ67412 (CA$9CLGID, CA$9EFM, CAEFFF, CAEFFT, CADSC1, CASEQ, CA$9INID)
	(SELECT 
