		CDTMPI
	FROM [SCDATA].FQ67413
	WHERE TRIM(CD$9CLGID) = TRIM(SourceCatalogID));
    		
	--NODE ITEMS COPY
	INSERT INTO [SCDATA].FQ67413I (IN$9CLGID, INUKID, INITM, INSEQ, INURAB, INURAT, INURCD, INURDT, INURRF, INUSER, INPID, INUPMJ, INUPMT, INJOBN)
