-- #param UniqueId			Unique Id

CREATE OR REPLACE PROCEDURE [SCLIBRARY].PRO_ExcSetBankAccountDefault
(
	AddressNumber	IN NUMBER,
	UniqueId		IN NUMBER,
	ResultData1 OUT GLOBALPKG.refcursor
)
AS
BEGIN
	/* Clear default BA */
	UPDATE 
