	    AND	TRIM(A.DRRT) = 'DT'
	    AND	NVL(SUBSTR(A.DRKY, 10 - CodeLength + 1, CodeLength),' ') NOT IN (SELECT	MRDCTO
			      	         FROM	[SCDATA].FQ674210 MKCR
			      	         WHERE 	TRIM(MR$9MKT) = TRIM(MarketingCode))
	    ORDER BY DRKY;
