				AND B.SDLNID = A.LineNumber 
				AND B.SD$9TYP = 1
			'|| CONS_WHERE_DYNAMIC ||N'
		) ';
	END	IF;


	SQL_DYNAMIC := FROM_DYNAMIC || N'
	, PAGING AS (
		SELECT
			B.OrderNumber,
			B.OrderType,
