			B.CurrencyMode,
			B.HoldOrderCode,
			B.Description1,
			B.Description2,
			ROW_NUMBER() OVER ( ORDER BY '|| SORT_DYNAMIC ||N') AS RNUM
		FROM CTE B
		' || INNER_DYNAMIC || N'
	)
	SELECT
		B.OrderNumber,
		B.OrderType,
		B.Company,
		B.LineNumber,
		B.LineType,
		B.UnitPrice,
		B.ForeignUnitPrice,
