	ORDER BY RNUM ';

	IF (AddressBookType = 0) THEN
		OPEN ResultData1 FOR SQL_DYNAMIC USING AddressNumber, FreightLnType, DiscountLnType, TrackingLnType, SurchargeLnType, TransactionDateFrom, TransactionDateTo,
