		(
			RegisterID,
			DrawerNumber,
			BatchNumber,
			SequenceNumber,
			LineNumber,
			PaymentInstrument,
			CurrencyCode,
			AmountTransact,
			UserId,
			'', /*Type Transaction*/
			UserReservedReference,
			UserReservedNumber,
			UserReservedAmount,
			UserReservedCode,
