    RegisterID      IN NCHAR,
	DrawerNumber    IN NUMBER,
    ResultData1 OUT GLOBALPKG.refcursor
)
AS
BEGIN
    OPEN ResultData1 FOR
        SELECT     
	        A.DR$9REGID AS RegisterID, 
	        A.DR$9DRAW AS DrawerNumber, 
	        A.DR$9DRST AS Status, 
