(
	MessageID       IN NCHAR,
	Description1    IN NCHAR,
	Description2    IN NCHAR,
    "Type"		    IN NCHAR,
    OneTimeOnly     IN NCHAR,
    Priority        IN NUMBER,
    EffectiveDate   IN NUMBER,
    ResultData1 OUT GLOBALPKG.refcursor
)
AS
